"use client";

import { useState } from "react";

export default function ProgressDownloadBtn() {
  const [active, setActive] = useState(false);
  const [done, setDone] = useState(false);
  const duration = 4000; // ms

  const handleClick = () => {
    if (active || done) return;
    setActive(true);
    setTimeout(() => {
      setActive(false);
      setDone(true);
    }, duration);
  };

  return (
    <div className="relative flex flex-col items-center">
      <button
        onClick={handleClick}
        className={`relative block rounded-md bg-white px-6 py-4 shadow-lg transition-transform duration-300
        ${active ? "animate-pulse" : ""} ${done ? "border-green-500" : "border-gray-300"}`}
      >
        <div className="flex items-center gap-3">
          <div className="relative flex h-6 w-6 items-center justify-center text-gray-500">
            <svg
              className="absolute h-5 w-5 text-white"
              viewBox="0 0 20 18"
              fill="currentColor"
            >
              <polygon points="8 0 12 0 12 9 15 9 10 14 5 9 8 9" />
            </svg>
            <svg
              className={`h-5 w-5 transition-colors duration-500 ${
                done ? "text-green-500" : "text-gray-500"
              }`}
              viewBox="0 0 20 18"
              fill="currentColor"
            >
              <path d="M4.8,0h10.3c0.9,0,1.7,0.6,1.9,1.4l2.6,8.9c0.2,0.6,0.2,1.2,0.1,1.8l-0.7,4.2c-0.2,1-1,1.7-2,1.7H2.8c-1,0-1.8-0.7-2-1.7l-0.7-4.2c-0.1-0.6-0.1-1.2,0.1-1.8L2.9,1.4C3.2,0.6,4,0,4.8,0z" />
            </svg>
          </div>
          <div
            className={`text-base font-medium transition-colors ${
              done ? "text-green-500" : "text-gray-800"
            }`}
          >
            {done ? "Done âœ…" : active ? "Downloading..." : "Download"}
          </div>
        </div>
        <div
          className={`absolute bottom-0 left-0 h-1 bg-green-500 transition-all duration-[${duration}ms] ${
            active ? "w-full" : "w-0"
          }`}
        />
      </button>
    </div>
  );
}
